import React, { Component } from 'react';
// import Auxiliary from '../../HOC/Auxiliary/Auxiliary';
import HomePage from '../../Components/HomePage/HomePage';
import Portfolio from '../../Components/Portfolio/Portfolio';
import Navbar from '../../Components/Navbar/Navbar';
import classes from './ContentManager.css';

class ContentManager extends Component {

    state = {
        show: false
    }

    ToggleContentHandler = () => {
        this.setState( ( prevState ) =>{
            return {show: !prevState.show}
        });
    }

    render () {
        return (
            <div className={classes.ContentManager}>
            {/* {this.state.show && <Navbar clicked={this.ToggleContentHandler}/>} */}
                <Navbar itemCliced={this.ToggleContentHandler}/>
                <HomePage open={this.state.show}/>
                {/* {this.state.show && <HomePage />}  */}
                <Portfolio />
            </div>
        )
    }

}

export default ContentManager;
